import React ,{useState,useRef,useEffect} from 'react' 
import PlayerDetails from './PlayerDetails'
import PlayerControl from './PlayerControl'


const Player = (props) => {
  const audioEl = useRef(null);
  const [isPlaying, setIsPlaying] =useState(false)

  useEffect(()=>{
    if(isPlaying){
      audioEl.current.Play();
    }else{
      audioEl.current.pause();
    }
  })

  const SkipSong= (forward = true)=>{
    if(forward){
      props.setCurrentSongIndex(()=>{ 
        let temp = props.setCurrentSongIndex;
        temp++;

        if(temp > props.song.length - 1){
          temp =0;
        }
            return temp;
      })
    }else{
      props.setCurrentSongIndex(()=>{
        let temp = props.setCurrentSongIndex;
        temp--;

        if(temp < 0  ){
          temp = props.songs.length - 1;
        }
            return temp;
      })

    }
  }


  return (
    <div className='c-player'>
        <audio src={props.songs[props.currentSongIndex].src} 
          ref={audioEl}> </audio>
      <h4>Playing Now</h4>
      <PlayerDetails song={props.songs[props.currentSongIndex]} />
      <PlayerControl isPlaying={isPlaying} 
      setIsPlaying={setIsPlaying}
      SkipSong = {SkipSong}

      />

      <p><strong>Next Up : </strong> {props.songs[props.nextSongIndex].title}  
      by {props.songs[props.nextSongIndex].artist}</p>
    </div>
  )
}

export default Player
