import React from 'react'

export default class State extends React.Component
{
    constructor(){
    super();
    this.state={
        name:'Harshit',
        email:'harshit@xyz'

    }

    updateState({
        name:'Nitish Bisht'
    })
    }
    render()
    {
        return(

            <div>
                <h1>Hellow {this.state.name}</h1>
                <h1> {this.state.email}</h1>
                <button onClick={()=>{this.updateState()}}>Update Name</button>
            </div>
            )
    }
}
